import React, { Component } from 'react';
//import logo from './logo.svg';
import 'bootswatch/dist/solar/bootstrap.min.css'; 
import './App.css';
import axios from "axios";

class App extends Component {
  state = {
    //resultFrequency = []
  };

// button is pressed, read user input
// pass to component that builds the url dynamically given factors
// url passed here 
  componentDidMount() {
    axios
      .get('https://newton.now.sh/simplify/')
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      })
  }

  render() {
    {/* if we implement a loading spinner
    if(!this.props.title) {
      return(null);
    }
  */}
    return(
      <div>
        <div class="page-header" id="banner">
          <h1>Note Frequency Math</h1>
          <p>Go Nimbly Interview Project by Amy Herz</p>
        </div>
        <div class="jumbotron">
          <div class="form-group">
            <label for="frequencyRange">Frequency Range</label>
            <input type="range" class="custom-range" id="frequencyRange"></input>
            <BaseFrequency number="440" />
            {/* add in designation underneath this to grab frequency and display note (middle C, C4) */}
            <SemiTones number="1" />
            <label class="col-form-label col-form-label-lg" for="resultFrequency">
              Resulting Frequency
            </label>
            <input class="form-control form-control-lg" type="text" placeholder="281.6" id="resultFrequency"></input>
            {/*<button type="button" class="btn btn-primary btn-lg btn-block" onClick={() =>}>
              Play note
            </button>
    */}
          </div>
        </div>
      </div>
    );
  }
}

/*  
  Starting with a base of A4 with a nice whole note frequency of 440.
  This frequency will remain unchanged as our point of reference.
*/
function BaseFrequency(props) {
  return (
    <div class="form-group">
      <fieldset>
        <label class="control-label" for="baseFrequency">Base Frequency (in Hertz)</label>
        <input class="form-control form-control-lg" type="text" placeholder="440" id="baseFrequency" readonly=""></input>
        <small id="baseFreqHelp" class="form-text text-muted">We are starting with the fifth A, called A4, with a frequency of 440Hz</small>
      </fieldset>
    </div>
  );
}

function SemiTones(props) {
  return (
    <div>
      <label class="col-form-label col-form-label-lg" for="userInput">Semitones</label>
      <input class="form-control form-control-lg" type="text" placeholder="1" id="userInput"></input>
    </div>
  );
}

class ResultNote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 440,   // begin with result equaling our initial note, A, at 440Hz
    };
  }

  state = {
    calculatedFrequency: []
  }

  componentDidMount() {
    fetch()
    .then(res => res.json())
    .then((data) => {
      this.setState({ calculatedFrequency: data })
    })
    .catch(console.log)
  }
}


export default App;
