import React, { Component } from 'react';
//import logo from './logo.svg';
import 'bootswatch/dist/solar/bootstrap.min.css'; 
import './App.css';
import axios from "axios";

class App extends Component {
  state = {
    //resultFrequency = []
  };

// button is pressed, read user input
// pass to component that builds the url dynamically given factors
// url passed here 
  componentDidMount() {
    axios
      .get('https://newton.now.sh/simplify/')
      .then(function(response) {
        console.log(response);
      })
      .catch(function(error) {
        console.log(error);
      })
  }

  render() {
    /*  TODO: implement a loading spinner
    if(!this.props.title) {
      return(null);
    }
    */
    return(
      <div>
        <div class="page-header" id="banner">
          <h1>Note Frequency Math</h1>
          <p>Go Nimbly Interview Project by Amy Herz</p>
        </div>
        <div class="jumbotron">
          <div class="form-group">
            <BaseInput number="440" />
            <SemitonesForm number="0" />
            <button type="button" class="btn btn-primary btn-lg btn-block">Calculate</button>
          </div>
          <div class="form-group">
            <Results number="440" />
          </div>
        </div>
      </div>
    );
  }
}

/*  
  Starting with a base of A4 with a nice integer note frequency of 440.
  This frequency will remain unchanged as our point of reference.
*/
function BaseInput(props) {
  const base = {freq:"440", note:"A4"};
  return (
    <div class="form-group">
      <fieldset>
        <label class="control-label" for="baseFrequency">Base Frequency (in Hertz)</label>
        <input class="form-control form-control-lg" type="number" placeholder="440" id="baseFrequency" readonly=""></input>
        <small id="baseFreqHelp" class="form-text text-muted">We are starting with the "Middle A" (A4) with a frequency of 440Hz</small>
      </fieldset>
    </div>
  );
}

/*  
  From our base note, user provides number of semitones, or half steps to travel from there.
  User can input positive number to travel up or negative number to travel down in pitch.
*/
class SemitonesForm extends Component {
  constructor(props) {
    super(props);
    this.state = {number: 0};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({freq: event.target.number})
  }

  handleSubmit(event) {
    // insert axios call to calculate result and pass to that component? 
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label class="col-form-label col-form-label-lg" for="userInput">Semitones</label>
          {/* 
            The musical scale goes from C0 to B8, giving us the range of -57 to 50 for semitones options
          */}
          <input class="form-control form-control-lg" type="number" placeholder="0" id="userInput" min="-57" max="50" step="1"></input>
          <small id="semitonesHelp" class="form-text text-muted">Enter how many semitones, or half steps you would like to add or subtract from A4 (integer numbers only ranging from -57 to 50).</small>
          <input type="submit" value="Calculate" />
        </div>
      </form>
    );
  } 
}

/*  
  Calculated resulting frequency/note to display along with other information.
*/
class Results extends Component {
  constructor(props) {
    super(props);
    this.state = {freq:"440", note:"A4", ratio:"1"};

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      //axios shit
    }));
  }

  render() {
    return (
      <div class="card text-white bg-success mb-3">
        <h3 div class="card-header">Results</h3>
        <div class="card-body">
          <h4 class="card-title">Consonance/Dissonance?</h4>
          <p class="card-text">After calculating....</p>
          <ul class="list-group list-group-flush">
            <li class="list-group-item">Note: {this.note}</li>
            <li class="list-group-item">Frequency: {this.freq}</li>
            <li class="list-group-item">Fraction: {this.ratio}</li>
          </ul>
          <div class="card-body">
            <button type="button" class="btn btn-outline-success" onClick={this.handleClick}>
              Play note</button>
          </div>
        </div>
      </div>
    );
  }

}

class ResultCalculation extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 440,   // begin with result equaling our initial note, A, at 440Hz
    };
  }

  state = {
    calculatedFrequency: []
  }

  componentDidMount() {
    fetch()
    .then(res => res.json())
    .then((data) => {
      this.setState({ calculatedFrequency: data })
    })
    .catch(console.log)
  }
}


export default App;
