import React, { Component } from 'react';
//import ReactDOM from 'react-dom';
import 'bootswatch/dist/solar/bootstrap.min.css'; 
import './App.css';
import axios from "axios";

class App extends Component {

  render() {
    /*  TODO: implement a loading spinner
    if(!this.props.title) {
      return(null);
    }
    */
    return(
      <div>
        <div className="page-header" id="banner">
          <h1>Note Frequency Math</h1>
          <p>Go Nimbly Interview Project by Amy Herz</p>
        </div>
        <div className="jumbotron">
          <div className="form-group">
            <BaseInput number="440" />
            <SemitonesForm />
          </div>
          <div className="form-group">
            <Results />
          </div>
        </div>
      </div>
    );
  }
}

/*  
  Starting with a base of A4 with a nice integer note frequency of 440.
  This frequency will remain unchanged as our point of reference.
*/
function BaseInput(props) {
  const base = {freq:"440", note:"A4"};
  return (
    <div className="form-group">
      <fieldset>
        <label className="control-label" for="baseFrequency">Base Frequency (in Hertz)</label>
        <input className="form-control form-control-lg" type="number" placeholder="440" id="baseFrequency" readOnly="true" value="440"></input>
        <small id="baseFreqHelp" className="form-text text-muted">We are starting with the "Middle A" (A4) with a frequency of 440Hz</small>
      </fieldset>
    </div>
  );
}

/*
  Simple function to set state of other class and pass to results
*/
function updateFrequency(freq) {
  this.setState({freq})
}

/*  
  From our base note, user provides number of semitones, or half steps to travel from there.
  User can input positive number to travel up or negative number to travel down in pitch.
*/
class SemitonesForm extends Component {
  constructor(props) {
    super(props);
    this.state = {number: 0, frequency: 440, logResult: 0, key: 49};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    //this.createPromise = this.createPromise.bind(this);
    //this.sendNewtonRequest = this.sendNewtonRequest.bind(this);
    //this.handleNewtonResponse = this.handleNewtonResponse.bind(this);
  }

  handleChange(event) {
    this.setState({number: event.target.value});
  }

  handleSubmit(event) {
    const freq = 440 * Math.pow(2, (this.state.number / 12));
    this.setState({frequency: freq});
    console.log('freq: ' + this.frequency);

    //const newtonUrl = 'https://newton.now.sh/simplify/440%2A2%5E(' + this.state.number + '(over)12)';
    //console.log('url for newton api:' + newtonUrl);
    const base = 'https://newton.now.sh/';
    // the expression we want is 440*2^({semitones}/12), however we need formatted url encoding
    //const expression = '440(2%5E(' + this.state.number + '(over)12))';
    //console.log('expression: ' + expression);
    fetch(base + 'log/2|' + this.state.number)
    .then(response => response.json())
    .then(
      (logResults) => {
        this.setState({logResult: logResults.result});
        console.log('log result: ', logResults);
        console.log('this.state.logResult: ' + this.state.logResult);
      }
    )
    .catch((error) => {
      console.log(error);
      console.log('error response: ' + error.response);
      console.log('error status: ' + error.response.status);
    })
    /*
    fetch('https://newton.now.sh/simplify/12(' + this.state.logResult + ')+49')
    .then(res => res.json())
    .then(
      (results) => {
        this.setState({key: results.result});
        this.passToResults(results.result);
        console.log(results);
      },
      (error) => {
        console.log(error);
        console.log('error response: ' + error.response);
        console.log('error status: ' + error.response.status);
      }
    )
    */
    /*
    const fetchNewton = async () => {
      const response = await fetch('https://newton.now.sh/simplify/440(2%5E(' + this.state.number + '(over)12))');
      const { results } = await response.json();
      console.log('results: ' + results);
      this.setState({
        expression: results.result
      });
    };
    fetchNewton();
    */

    console.log('this.expression after round 1: ' + this.state.expression);
    event.preventDefault();
  }

  passToResults(freq) {
    updateFrequency(freq)
  }
/*
  createPromise() {
    return new Promise(function (resolve, reject) {
      let url = 'https://newton.now.sh/simplify/440%2A2%5E(' + this.state.number + '(over)12)';

      url.onload = function() {
        if(this.status >= 200 && this.status < 300) {
          resolve(xhr.responseText);
        } else {
          reject({status: this.status, msg: xhr.statusText});
        }
      };

      xhr.onerror = function() {
        reject({status: this.status, msg: xhr.statusText});
      };

      xhr.open("GET", url);
      xhr.send();
    });
  }

  sendNewtonRequest () {
    this.createPromise()
    .then(response => callback(handleNewtonResponse(JSON.parse(response))))
    .catch(error => console.error('NewtonMath error: ', error.status, error));
  }

  handleNewtonResponse (response) {
    if(response.hasOwnProperty('error')) {
      console.error(response['error']);
    } else {
      let result = response['result'];

      try {
        return JSON.parse(result);
      } catch (e) {
        if(result.constructor == String && result.toLowerCase() == 'nan') {
          return NaN;
        } else {
          return result;
        }
      }
    }
  }
*/


  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <div>
          <label className="col-form-label col-form-label-lg" for="userInput">Semitones
            {/*The musical scale goes from C0 to B8, giving us the range of -57 to 50 for semitones options*/}
            <input className="form-control form-control-lg" type="number" placeholder="0" id="userInput" min="-57" max="50" step="1" value={this.state.number} onChange={this.handleChange}></input>
          </label>
          <small id="semitonesHelp" className="form-text text-muted">Enter how many semitones, or half steps you would like to add or subtract from A4 (integer numbers only ranging from -57 to 50).</small>
          <input type="submit" className="btn btn-primary btn-lg btn-block" value="Calculate" />
          <label className="col-form-label col-form-label-lg" for="userInput">Key on Piano
            <input className="form-control form-control-lg" type="text" placeholder="nothing" id="newtResult" readOnly="true" value={this.state.logResult}></input>
          </label>
        </div>
      </form>
    );
  } 
}


/*  
  Calculated resulting frequency/note to display along with other information.
*/
class Results extends Component {
  constructor(props) {
    super(props);
    this.state = {freq:"440", note:"A4", ratio:"1"};

    this.handleClick = this.handleClick.bind(this);
    updateFrequency = updateFrequency.bind(this);
  }

  handleClick() {
    this.setState(state => ({
      //axios shit
    }));
  }

  render() {
    return (
      <div class="card text-white bg-success mb-3">
        <h3 div class="card-header">Results</h3>
        <div class="card-body">
          <h4 class="card-title">Consonance/Dissonance?</h4>
          <p class="card-text">After calculating....</p>
          <ul class="list-group list-group-flush">
            <li class="list-group-item">Note: {this.state.note}</li>
            <li class="list-group-item">Frequency: {this.state.freq}</li>
            <li class="list-group-item">Fraction: {this.state.ratio}</li>
          </ul>
          <div class="card-body">
            <button type="button" class="btn btn-outline-success" onClick={this.handleClick}>
              Play note</button>
          </div>
        </div>
      </div>
    );
  }

}

